import React, { useState, useEffect} from 'react';
import { HeroContainer,
         HeroBg,
         ImgBg,
         HeroContent,
         HeroH1,
         HeroP,
         HeroBtnWrapper,
         ArrowForward,
         ArrowRight,
         Buttong,
          } from './HeroElements';
import background from "../../images/image1.jpg"; 
import Aos from 'aos';
import "aos/dist/aos.css";
import { Button } from '../ButtonElements'
     

const HeroSection = () => {

    

    const [hover, setHover] = useState(false)

    const onHover = ()=>{
        setHover(!hover)
    }

    useEffect(()=>{
        Aos.init({duration: 2000 });
    },[])

    return (
        <HeroContainer id='home'>
            <HeroBg>
                <ImgBg style={{ backgroundImage: `url(${background})`}}/>
            </HeroBg>
            <HeroContent >
                <HeroH1 data-aos="fade-right">Do the Work You Love</HeroH1>
                <HeroP data-aos="fade-left">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
                                Curabitur ac lacus at felis rutrum ullamcorper quis ac arcu.
                </HeroP>
                <HeroBtnWrapper data-aos="fade-up">
                    <Buttong to='signup' onMouseEnter = {onHover} 
                    onMouseLeave = {onHover}
 >
                        Get started {hover ? <ArrowForward/>:<ArrowRight/>}
                    </Buttong>
                </HeroBtnWrapper>
            </HeroContent>

        </HeroContainer>
            
        
    );
};

export default HeroSection;
