import React, { useState} from 'react';
 <NavItem>
                          <NavLinks to="services"
                                    smooth={true} 
                                    duration={500} 
                                    spy={true} 
                                    exact='true' 
                                    offset={-80}>Resources</NavLinks>
                      </NavItem>
import { HeroContainer,
         HeroBg,
         ImgBg,
         HeroContent,
         HeroH1,
         HeroP,
         HeroBtnWrapper,
         ArrowForward,
         ArrowRight,
          } from './HeroElements';
import background from "../../images/image1.jpg"; 
     

const HeroSection = () => {

    const [hover, setHover] = useState(false)

    const onHover = ()=>{
        setHover(!hover)
    }
    return (
        <HeroContainer id='home'>
            <HeroBg>
                <ImgBg style={{ backgroundImage: `url(${background})`}}/>
            </HeroBg>
            <HeroContent>
                <HeroH1>Virtual Banking Made Easy</HeroH1>
                <HeroP>
                    Sign up for a new account today and recieve
                    $250 in credit towards your next payment
                </HeroP>
                <HeroBtnWrapper>
                    <Button to='signup' onMouseEnter = {onHover} 
                    onMouseLeave = {onHover}
                    primary='true'
                    dark='true'
                    smooth={true} 
                    duration={500} 
                    spy={true} 
                    exact='true' 
                    offset={-80}>
                        Get started {hover ? <ArrowForward/>:<ArrowRight/>}
                    </Button>
                </HeroBtnWrapper>
            </HeroContent>

        </HeroContainer>
            
        
    );
};

export default HeroSection;
