import React, { useState} from 'react';
import { Button } from '../ButtonElements';
import { HeroContainer,
         HeroBg,
         ImgBg,
         HeroContent,
         HeroH1,
         HeroP,
         HeroBtnWrapper,
         ArrowForward,
         ArrowRight,
         Bu
          } from './HeroElements';
import background from "../../images/image1.jpg"; 
     

const HeroSection = () => {

    const [hover, setHover] = useState(false)

    const onHover = ()=>{
        setHover(!hover)
    }
    return (
        <HeroContainer id='home'>
            <HeroBg>
                <ImgBg style={{ backgroundImage: `url(${background})`}}/>
            </HeroBg>
            <HeroContent>
                <HeroH1>Do the Work You Love</HeroH1>
                <HeroP>
                    Sign up for a new account today and recieve
                    $250 in credit towards your next payment
                </HeroP>
                <HeroBtnWrapper>
                    <Buttong to='signup' onMouseEnter = {onHover} 
                    onMouseLeave = {onHover}
                    primary='true'
                    dark={true}
                    smooth={true} 
                    duration={500} 
                    spy={true} 
                    exact='true' 
                    offset={-80}>
                        Get started {hover ? <ArrowForward/>:<ArrowRight/>}
                    </Buttong>
                </HeroBtnWrapper>
            </HeroContent>

        </HeroContainer>
            
        
    );
};

export default HeroSection;
