import styled from "styled-components";
import { Link } from 'react-router-dom';

export const Button = styled(Link)`
border-radius: 6px;
background: '';
padding: 18px 48px;
color: #000;
outline: none;
 border: 2px solid black;
 cursor: pointer;
 font-size: 18px;
 display: flex;
 justify-content: center;
 align-items: center;
 transition: all 0.2s ease-in-out;
 text-decoration: none;

 &:hover{
     transition: all 0.2s ease-in-out;
     background: #fff;
     color: #01BF71;
 }
`
